var pin1 = ""
var pin2 = ""
var pin3 = ""
var pin4 = ""
var pin5 = ""

function findPin(arr) {
    let coords = { x: -2, y: 0 }
    for (var i = 0; i < arr.length; i++) {
        var direction = arr[i];
        switch (direction) {
            case "U":
                if (coords.y < 1 && Math.abs(coords.x) != 2|| coords.x == 0  && coords.y < 2) {
                    coords.y += 1
                }
                break;
            case "D":
                if (coords.y > -1 && Math.abs(coords.x) != 2|| coords.x == 0  && coords.y > -2) {
                    coords.y -= 1
                }
                break;
            case "L":
                if (coords.x > -1 && Math.abs(coords.y) != 2|| coords.y == 0  && coords.x > -2) {
                    coords.x -= 1
                }
                break;
            case "R":
                if (coords.x < 1 && Math.abs(coords.y) != 2|| coords.y == 0  && coords.x < 2) {
                    coords.x += 1
                }
                break;
        }
    }
    console.log("("+coords.x+","+coords.y+")"+" Press: "+findButton(coords))
}

function findButton(coords){
    let stringCoords = "X"+coords.x+"Y"+coords.y
    let button;
    switch(stringCoords){
        case "X2Y0":
            button = "9"
            break;
        case "X1Y0":
            button = "8"
            break;
        case "X0Y0":
            button = "7"
            break;
        case "X-1Y0":
            button = "6"
            break;
        case "X-2Y0":
            button = "5"
            break;
        case "X1Y-1":
            button = "C"
            break;
        case "X0Y-1":
            button = "B"
            break;
        case "X-1Y-1":
            button = "A"
            break;
        case "X1Y1":
            button = "4"
            break;
        case "X0Y1":
            button = "3"
            break;
        case "X-1Y1":
            button = "2"
            break;
        case "X0Y2":
            button = "1"
            break;
        case "X0Y-2":
            button = "D"
            break;
    }
    return button
}

findPin(pin1)
findPin(pin2)
findPin(pin3)
findPin(pin4)
findPin(pin5)